    return (
        <div className="App">
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    id="firstName"
                    name="firstName"
                    value={firstName}
                    placeholder="First Name"
                    onChange={(event) =>
                        setFirstName(event.target.value)
                    }
                />

                <br />
