  // useEffect(() => {
  //   fetch("http://localhost:8000/message")
  //       .then((res) => res.json())
  //       .then((data) => setMessage(data.message));
  // }, []);

    return (

        <div>
            <form onSubmit={handleSubmit}>
                <label>
                    Name:
                    <input type="text" value={this.state.value} name="name" onChange={handleChange} />
                </label>
                <input type="submit" value="Submit" />
            </form>
        </div>
    );
};

export default Home;
